mapper.insert:
{call insert_user_ps(
    #{username, mode=IN, jdbcType=VARCHAR},
    #{password, mode=IN, jdbcType=VARCHAR},
    #{name, mode=IN, jdbcType=VARCHAR},
    #{email, mode=IN, jdbcType=VARCHAR},
    #{userId, mode=OUT, jdbcType=INTEGER}
)}

mapper.select:
select at.user_id, at.username, at.password, at.name, at.email,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name from auth_tb at LEFT OUTER JOIN user_role_tb urt on (urt.user_id = at.user_id) LEFT OUTER JOIN role_tb rt on (rt.role_id = urt.role_id) where at.username = #{username}

select at.user_id, at.username, at.password, at.name, at.email,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name from auth_tb at LEFT OUTER JOIN user_role_tb urt on (urt.user_id = at.user_id) LEFT OUTER JOIN role_tb rt on (rt.role_id = urt.role_id) where at.user_id = #{userId}
